import React, { useEffect, useState } from "react";
import Search from "./components/search";
import Video from "./components/video";
import "./app.css";

const App = (props) => {
  const [videos, setVideos] = useState([]);
  useEffect(() => {
    const requestOptions = {
      method: "GET",
      redirect: "follow",
    };

    fetch(
      "https://youtube.googleapis.com/youtube/v3/videos?part=snippet&chart=mostPopular&maxResults=25&key=AIzaSyCZhmvN-I3R8J0z_kGPqOpOI07eE4VQ_1w",
      requestOptions
    )
      .then((response) => response.json()) //text를 json으로
      .then((result) => {
        const videoTitle = result.items[0].snippet.title;
        const channelTitle = result.items[0].snippet.channelTitle;
        const thumbnailUrl = result.items[0].snippet.thumbnails.high.url;
        const itemLemngth = result.items.length; //추천하는 총동영상수 25개
      })
      .catch((error) => console.log("error", error));
  }, []);

  const handleSearch = (inputValue) => {
    const requestOptions = {
      method: "GET",
      redirect: "follow",
    };

    fetch(
      `https://youtube.googleapis.com/youtube/v3/search?part=snippet&maxResults=25&q=${inputValue}&key=AIzaSyCZhmvN-I3R8J0z_kGPqOpOI07eE4VQ_1w`,
      requestOptions
    )
      .then((response) => response.json())
      .then((result) => console.log(result))
      .catch((error) => console.log("error", error));
  };
  return (
    <div className="appWrapper">
      <Search onAdd={handleSearch}></Search>
      <div className="VideoWrapper">
        <Video></Video>
      </div>
    </div>
  );
};

export default App;
